import React, { createContext, useState, useEffect } from "react";

import { BoxLoading } from "react-loadingg";

import { useMethods } from "react-use";

import categoryService from '../services/CategoryService';




const initialState = {

  allCategories: [],

};


  function createMethods(state) {
    return {
      reset() {
        return initialState;
      },

      initCategories(allCategories) {
        return { ...state, allCategories };
      },

      /**
       * ! USERS/ADMIN -- REDUCERS 
       */
      addNewCategory(user){
        return {...state, allUsers:state.allUsers.concat(user)}
      },
      deleteCategory(id){
        return {...state, allUsers:state.allUsers.filter((u=>id!==u.id))}
      },
      editCategory(user){
        const updatedUsers =[]
        // eslint-disable-next-line array-callback-return
        state.allUsers.map((item)=>{
          if(user.id === item.id)updatedUsers.push(user);
          else updatedUsers.push(item)
        })
        return {...state,allUsers:updatedUsers}
        },

    };
  }

export const AppContext = createContext();

export default ({ children }) => {
  const [loaded, setLoaded] = useState(false);
  const [store, methods] = useMethods(createMethods, initialState);




	useEffect(async() => {
        try{

            const  allCategories=await categoryService.allCatgories()
                            await methods.initCategories(allCategories.data)

            setLoaded(true)
        }
        catch(err){
            console.log(err)
            setLoaded(true)
        }


	}, []);
	return (
		<div>
			{!loaded ? (
				<BoxLoading />
			) : (
				<AppContext.Provider
					value={{
						store,methods
					}}
				>
					{children}
				</AppContext.Provider>
			)}
		</div>
	);
};
