import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCoffee, faUser } from "@fortawesome/free-solid-svg-icons";
import "./AdminStyles.css";
export default function Sidebar(props) {
  const activeDisplay = {
    backgroundColor: "blueviolet",
  };



  return (
    <ul className="list">
      <li
        className="list-group-item m-0 p-0 custom-side-bar-li"
         style={(props.activeComponent === "Dashboard")  ? activeDisplay: null }

      >
        <a className="nav-link "
        onClick={(e)=>props.handleClick(e)}
        id="Dashboard">
          <FontAwesomeIcon icon={faUser} className="mr-2" /> Dashboard
        </a>
      </li>
      <li className="list-group-item m-0 p-0 custom-side-bar-li"
       style={(props.activeComponent === "UserPage")  ? activeDisplay: null }>
        <a className="nav-link "
        onClick={(e)=>props.handleClick(e)}
        id="UserPage">
          <FontAwesomeIcon icon={faUser} className="mr-2" />
          Users
        </a>
      </li>
      <li className="list-group-item m-0 p-0 custom-side-bar-li"
       style={(props.activeComponent === "AdminPage")  ? activeDisplay: null }>
        <a className="nav-link "
        onClick={(e)=>props.handleClick(e)}
        id="AdminPage">
          <FontAwesomeIcon icon={faUser} className="mr-2" />
          Admins
        </a>
      </li>
      <li className="list-group-item m-0 p-0 custom-side-bar-li"
       style={(props.activeComponent === "ProductPage")  ? activeDisplay: null }>
        <a className="nav-link "
        onClick={(e)=>props.handleClick(e)}
        id="ProductPage">
          <FontAwesomeIcon icon={faUser} className="mr-2" />
          Products
        </a>
      </li>
      <li className="list-group-item m-0 p-0 custom-side-bar-li"
       style={(props.activeComponent === "CategoryPage")  ? activeDisplay: null }>
        <a className="nav-link "
        onClick={(e)=>props.handleClick(e)}
        id="CategoryPage">
          <FontAwesomeIcon icon={faUser} className="mr-2" />
          Categories
        </a>
      </li>
    </ul>
  );
}
