/* eslint-disable no-unused-vars */
/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable no-underscore-dangle */
import React from 'react';

import EditIcon from '@material-ui/icons/Delete';

import DeleteIcon from '@material-ui/icons/Edit';

import '../../../Styles/General.css';


import {appContext} from '../../../../context/appContext';

export default function AllCategories() {

  const {store,methods}=React.useContext(appContext)
  // css
  const catImageCss = {
    // height:"100px",
    width: '100px',
  };


  //  operations
  const deleteCategory = (catName) => {
    console.log(catName);
    // CategoryService.deleteCatgeory(catName)
    //   .then((res) => {
    //     if(res.success)  dispatch({ type: 'deleteCategory', payload: catName });    
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
  };

  const editCategory = (catName) => {
    console.log(catName);
  };
  return (
    <div>
      <table className="table">
        <thead>
          <tr>
            <th className="text-12" scope="col">
              Name
            </th>
            <th className="text-12" scope="col">
              Description
            </th>
            <th className="text-12" scope="col">
              Image
            </th>
            <th className="text-12" scope="col">
              Operations
            </th>
          </tr>
        </thead>
        <tbody>
          {store && store.allCategories && store.allCategories.map((item) => {
            return (
              <tr key={item._id}>
                <th className="text-12">{item.catName}</th>
                <td className="text-12 ">{item.catDescription}</td>
                <td className="text-12">
                  <img
                  alt=''
                    className="img-fluid"
                    style={catImageCss}
                    src={`http://localhost:5000/${  item.catImagePath}`}
                  />
                </td>
                <td
                  className="flex-row text-12 d-flex justify-content-between align-items-center"
                >
                  <span
                    className="badge badge-danger"
                    id={item.catName}
                    onClick={() => {
                      deleteCategory(item.catName);
                    }}
                  >
                  <DeleteIcon />
                  </span>
                  <span
                    className="badge badge-success"
                    id={item.catName}
                    onClick={() => {
                      editCategory(item.catName);
                    }}
                  >
                   <EditIcon />
                  </span>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
