/* eslint-disable no-unused-vars */
import React from 'react';
import { AppContext } from '../../context/appContext';
import SERVER_URL from '../../environment'

export default function ShopPage() {
	const { store } = React.useContext(AppContext);
	console.log('====================================');
	console.log(store);
	console.log('====================================');
	const [state, setState] = React.useState({
		allCategories: store.allCategories,
		allProducts: store.allProducts,
	});

	return (
		<div className='container-fluid'>
			<div className='row'>

					{state.allProducts?.length > 0 &&
						state.allProducts?.map((item) => {
							return (

									<div className='p-3  col-md-4'>
                                        <div className="card">
                                        <div className="text-center card-header">{item.name}</div>
										<div className='card-img'>
											<img alt='' src={`${SERVER_URL}/${item.img}`} className="img-thumbnail" />
										</div>
                                        <div className="card-body">
                                           <p> Category : {item.cat}</p>
                                        </div>
                                        </div>
									</div>

							);
						})}
				</div>
			</div>

	);
}
